mapper.select:
select title, day, mem, genre
	from newslist


		day = ${key}


		title = ${key}


		genre = ${key}

select title, day, mem, contente
	from newlist


			title = ${key}

mapper.insert:
insert into newslist(num , title , day , mem , genre , contente)
	values{seq_newslist.nextval,#{title},TO_CHAR(SYSDATE,'YYYY-NN-DD')
			, #{mem} , #{genre}
